export class DegTrig {
  static toDegrees(rad: number): number {
    return (rad * 180) / Math.PI;
  }

  static toRadians(deg: number): number {
    return (deg * Math.PI) / 180;
  }

  static sin(deg: number): number {
    return Math.sin(this.toRadians(deg));
  }

  static cos(deg: number): number {
    return Math.cos(this.toRadians(deg));
  }

  static tan(deg: number): number {
    return Math.tan(this.toRadians(deg));
  }

  static asin(x: number): number {
    return this.toDegrees(Math.asin(x));
  }

  static acos(x: number): number {
    return this.toDegrees(Math.acos(x));
  }

  static atan(x: number): number {
    return this.toDegrees(Math.atan(x));
  }

  static atan2(x: number, y: number): number {
    return this.toDegrees(Math.atan2(x, y));
  }
}
